import React from 'react'
import { Spinner } from 'react-bootstrap';
import PokemonData from '../components/PokemonData';
import Search from '../components/Search';
import { fetchPokemon } from '../services/fetch';

const spinnerStyle = {
    width: '10rem',
    height: '10rem',
    borderWidth: '1rem',
    color: 'blue',
}

const spinnerWarapperStyle= {
    textAlign:'center',
    marginTop: '60px',
}

export default function HomePage(){

    const [pokemon, setPokemon]= React.useState();
    const [loading, setLoading]= React.useState(false);

    const getPokemon = async (name)=> {
        if (!name){
            console.log('Please enter a name to search for!')
        }
        setLoading(true);
        setTimeout( async () => {
            const response = await fetchPokemon(name);
            const results= await response.json();
            setPokemon(results);
            setLoading(false);
        }, 1500);
    }

    return (
        <div>
            <Search getPokemon = {getPokemon}/>
            {loading ? (
                <div style={spinnerWarapperStyle}>
                    <Spinner animation = "border" style= {spinnerStyle}/>
                </div>
            ):null}
            {!loading && pokemon ? (
                <PokemonData 

                name={pokemon.name} 
                sprite ={pokemon.sprites.front_default}
                abilities = {pokemon.abilities}
                stats= {pokemon.stats}
                types= {pokemon.types}/>
            ): null}

        </div>
    )
}
